import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default {
    // 全局状态管理
    state: {
        menuList: [],
        permList: [],

        hasRoutes: false,

        editableTabsValue: 'Index',
        editableTabs: [{
            title: '首页',
            name: 'Index',
        }],
    },

    mutations: {

        setMenuList(state, menus) {
            state.menuList = menus
        },
        setPermList(state, perms) {
            state.permList = perms
        },

        changeRouteStatus(state, hasRoutes) {
            state.hasRoute = hasRoutes
        }

    },

    actions: {
    },

}
