const Mock = require('mockjs')

const Random = Mock.Random

let Result = {
    code: 200,
    msg: '操作成功',
    data: null
}

Mock.mock('/captcha', 'get', () => {

    Result.data = {
        token: Random.string(32),
        captchaImg: Random.dataImage('100x36', 'xzh999')
    }

    return Result
})

Mock.mock('/login', 'post', () => {
    //无法在 header 中传入数 jwt

    Result.code = 400
    Result.msg = "验证码错误"

    return Result
})

Mock.mock('/sys/userInfo', 'get', () => {

    Result.data = {
        id: "1",
        username: "test",
        avatar: "https://image-1300566513.cos.ap-guangzhou.myqcloud.com/upload/images/5a9f48118166308daba8b6da7e466aab.jpg?t=10:46:53"
    }

    return Result
})


Mock.mock('/logout', 'post', () => {

    return Result
})

Mock.mock('/sys/menu/nav', 'get', () => {

    let nav = [
        {
            title: '系统管理',
            name: 'SysManga',
            icon: 'el-icon-s-operation',
            path: '',
            component: '',
            children: [
                {
                    title: '用户管理',
                    name: 'SysUser',
                    icon: 'el-icon-s-custom',
                    path: '/sys/users',
                    component: 'sys/User',
                    children : []
                },
                {
                    title: '角色管理',
                    name: 'SysRole',
                    icon: 'el-icon-rank',
                    path: '/sys/roles',
                    component: 'sys/Role',
                    children : []
                },
                {
                    title: '菜单管理',
                    name: 'SysMenu',
                    icon: 'el-icon-menu',
                    path: '/sys/menus',
                    component: 'sys/Menu',
                    children : []
                },
            ]
        },
        {
            title: '系统工具',
            name: 'SysTools',
            icon: 'el-icon-s-tools',
            path: '',
            component: '',
            children: [
                {
                    title: '数字字典',
                    name: 'SysDict',
                    icon: 'el-icon-s-order',
                    path: '/sys/dicts',
                    component: 'sys/Dict',
                    children : []
                }
            ]
        }
    ]
    let authorities = []

    Result.data = {
        nav : nav,
        authorities : authorities
    }

    return Result
})
